        public static void Draw(int room, List<MonsterEntity> monsterList)
        {
            Console.SetCursorPosition(0, 0);
            for(int row = 0; row < 25; row++)
            {
                for(int col = 0; col < 70; col++)
                {
                    if (mapArray[room, row, col] == 1)
                        Console.Write('█');
                    else if (mapArray[room, row, col] == 0)
                        Console.Write(' ');
                    else if (mapArray[room, row, col] == 2)
                        Console.Write('.');
                    else if (mapArray[room, row, col] == 3)
                        Console.Write('/');
                    else if (mapArray[room, row, col] == 4)
                        Console.Write('X');
                    else if (mapArray[room, row, col] == 5)
                        Console.Write('\\');
                    else if (mapArray[room, row, col] == 6)
                        Console.Write('J');

                }
                Console.Write('\n');
            }
            for(int i = 0; i < monsterList.Count; i++)
            {
                Console.SetCursorPosition(monsterList[i].position.x, monsterList[i].position.y);
                Console.Write("o");
            }
            Console.SetCursorPosition(player.position.x, player.position.y);
            Console.Write("W");
        }
        public static void OpenDoor()
        {
            for (int row = 0; row < 25; row++)
            {
                for (int col = 0; col < 70; col++)
                {
                    if (mapArray[0, row, col] == 4)
                        mapArray[0, row, col] = 0;
                }
            }
        }
        public static void SetPlayer(Player newPlayer)
        {
            player = newPlayer;
        }
        public static bool NearJaskier()
        {
            //[5, 63]
            if(player.position.x <= 64 && player.position.x >= 62 &&
                player.position.y <= 6 && player.position.y >= 4)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
            
    }
